mapper.select:
SELECT T.RNUM, T.LPROD_GU, T.LPROD_NM , T.BUYER_ID, T.BUYER_NAME, T.BUYER_LGU FROM ( SELECT ROW_NUMBER() OVER(ORDER BY LPROD_NM, BUYER_ID) RNUM , L.LPROD_GU , L.LPROD_NM , B.BUYER_ID , B.BUYER_NAME , B.BUYER_LGU FROM LPROD L INNER JOIN BUYER B ON(L.LPROD_GU = B.BUYER_LGU) WHERE 1 = 1
AND (L.LPROD_GU LIKE '%'||#{keyWord}||'%' OR L.LPROD_NM LIKE '%'||#{keyWord}||'%' OR B.BUYER_ID LIKE '%'||#{keyWord}||'%' OR B.BUYER_NAME LIKE '%'||#{keyWord}||'%')
) T WHERE T.RNUM BETWEEN #{currentPage}*7-6 AND #{currentPage}*7 ORDER BY BUYER_ID

mapper.update:
UPDATE BUYER
   SET BUYER_MAIL = #{buyerMail}, BUYER_COMTEL = #{buyerComtel},
       BUYER_CHARGER = #{buyerCharger}, BUYER_IMG = #{buyerImg}
 WHERE BUYER_ID = #{buyerId}
